SELECT first_name, last_name, grade_math_pass, grade_science_pass, grade_english_pass 
FROM students 
WHERE first_name = 'Olivia' AND is_active = 1;

SELECT * FROM students 
WHERE first_name = 'Olivia' AND last_name = 'Hudson' 
AND grade_math_pass = 0 AND grade_science_pass = 0;

SELECT first_name, last_name 
FROM students 
WHERE first_name = 'Olivia' AND last_name = 'Hudson';

UPDATE students 
SET is_active = 1 
WHERE first_name = 'Olivia';

SELECT first_name, last_name 
FROM students 
WHERE is_active = 1 AND is_highschool = 1 AND speaks_english = 1;

SELECT first_name, last_name 
FROM students 
WHERE is_active = 1 AND (grade_math_pass = 1 OR grade_science_pass = 1 OR grade_english_pass = 1);

SELECT first_name, last_name 
FROM students 
WHERE is_active = 1 AND (speaks_english = 1 OR speaks_spanish = 1);
