SELECT first_name, last_name, grade_math_pass, grade_science_pass, grade_english_pass 
FROM students 
WHERE first_name = 'Olivia' AND is_active = 1;

UPDATE students 
SET is_active = 1 
WHERE first_name = 'Olivia';

SELECT first_name, last_name 
FROM students 
WHERE is_active = 1 AND (speaks_english = 1 OR speaks_spanish = 1);
